import { Box, Flex, Image, Text } from "@chakra-ui/react";
import shrek from "../../images/shrek.jfif";
import esh from "../../images/esh.jfif";
import fiona from "../../images/Fiona.png";
import fandom from "../../images/fandom.webp";
import feya from "../../images/feya.png";
import godfather from "../../images/godfather.png";
import alpachino from "../../images/alpachino.png";
import hz from "../../images/hz.png";
import fredo from "../../images/fredo.png";
import sonny from "../../images/sonny.png";
import React, { useState, useEffect } from "react";
import { shuffleArray } from "../../helpers/shuffleArray";

const images = [
  fiona,
  shrek,
  esh,
  fandom,
  feya,
  godfather,
  alpachino,
  hz,
  fredo,
  sonny,
];

export default function Header() {
  const [firstOpened, setFirstOpened] = useState(null);
  const [secondOpened, setSecondOpened] = useState(null);
  const [thirdOpened, setThirdOpened] = useState(null);
  const [gameImages, setGameImages] = useState([]);

  useEffect(() => {
    const shuffledImages = shuffleArray(images.concat(images));
    setGameImages(shuffledImages);
  }, []);

  const handleCardClick = (index) => {
    if (firstOpened === null) {
      setFirstOpened(index);
    } else if (secondOpened === null && index !== firstOpened) {
      setSecondOpened(index);
      checkMatch(index);
    } else if (thirdOpened === null && index !== firstOpened && index !== secondOpened) {
      setThirdOpened(index);
      checkMatch(index, true);
    }
  };

  const checkMatch = (index, isThirdCard = false) => {
    if (!isThirdCard) {
      if (gameImages[firstOpened] !== gameImages[index]) {
        setTimeout(() => {
          setFirstOpened(null);
          setSecondOpened(null);
        }, 1000);
      }
    } else {
      if (gameImages[firstOpened] !== gameImages[index] && gameImages[secondOpened] !== gameImages[index]) {
        setTimeout(() => {
          setFirstOpened(null);
          setSecondOpened(null);
        }, 1000);
      } else {
        console.log('Совпадение найдено!');
      }
    }
  };

  const isOpen = (index) => {
    return index === firstOpened || index === secondOpened || index === thirdOpened;
  };

  return (
    <Box mt="90" ml="400" bg="green" width="700px" height="620px">
      <Box>
        <Text
          fontSize="50px"
          fontWeight="1000"
          fontFamily="Libre Baskerville"
          pt="6"
          textAlign="center"
          color="white"
        >
          Игра на память
        </Text>
      </Box>
      <Flex justifyContent="space-between" wrap="wrap" p="6">
        {gameImages.map((img, index) => {
          return (
            <Box
              key={index}
              mt={5}
              onClick={() => handleCardClick(index)}
              display="flex"
              justifyContent="center"
              alignItems="center"
              bg="white"
              width="120px"
              height="100px"
              border={isOpen(index) ? "2px solid red" : "2px solid transparent"}
            >
              <Image
                style={{ display: isOpen(index) ? "block" : "none" }}
                id="shrek"
                width="110px"
                height="90px"
                src={img}
              />
            </Box>
          );
        })}
      </Flex>
    </Box>
  );
}
