import {
    Box,
    Flex,
    Image,
    Text,
  } from "@chakra-ui/react";
  import shrek from "../../images/shrek.jfif";
  import esh from "../../images/esh.jfif";
  import fiona from "../../images/Fiona.png";
  import fandom from "../../images/fandom.webp";
  import feya from "../../images/feya.png";
  import godfather from "../../images/godfather.png";
  import alpachino from "../../images/alpachino.png";
  import hz from "../../images/hz.png";
  import fredo from "../../images/fredo.png";
  import sonny from "../../images/sonny.png";
  import React, { useState, useEffect } from "react";
  
  const images = [
    fiona,
    shrek,
    esh,
    fandom,
    feya,
    godfather,
    alpachino,
    hz,
    fredo,
    sonny,
  ];
  
  function shuffleArray(array) {
    const shuffledArray = [...array]; // Создаем копию массива
    for (let i = shuffledArray.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [shuffledArray[i], shuffledArray[j]] = [shuffledArray[j], shuffledArray[i]];
    }
    return shuffledArray;
  }
  
  export default function Header() {
    const [displayStyle, setDisplayStyle] = useState("none");
    const [gameImages, setGameImages] = useState([]);
  
    useEffect(() => {
      const shuffledImages = shuffleArray(images.concat(images));
      setGameImages(shuffledImages);
    }, []); 
  
    const shrekhidden = () => {
      setDisplayStyle("block");
      console.log(888);
    };
  
    return (
      <Box mt="90" ml="400" bg="green" width="700px" height="620px">
        <Box>
          <Text
            fontSize="50px"
            fontWeight="1000"
            fontFamily="Libre Baskerville"
            pt="6"
            textAlign="center"
            color="white"
          >
            Memory game
          </Text>
        </Box>
        <Flex justifyContent="space-between" wrap="wrap" p="6">
          {gameImages.map((img, index) => (
            <Box
              key={index} // Уникальный ключ для каждого элемента списка
              mt={5}
              onClick={shrekhidden}
              border="2px solid red"
              display="flex"
              justifyContent="center"
              alignItems="center"
              bg="white"
              width="120px"
              height="100px"
            >
              <Image
                style={{ display: displayStyle }}
                id="shrek"
                width="110px"
                height="90px"
                src={img}
              />
            </Box>
          ))}
        </Flex>
      </Box>
    );
  }
